//sb-5mjyk27287097@personal.example.com
import React, { useState, useEffect } from 'react'
import { Button, Row, Col, ListGroup, Image, Card } from 'react-bootstrap'
import { Link,useParams,useNavigate } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { PayPalButton } from 'react-paypal-button-v2'
import Messageorderscreen from './Messageorderscreen'
import Loader from './Loader'
import { getOrderDetails,payOrder } from '../actions/Orderactions'
import { ORDER_PAY_RESET, ORDER_DELIVER_RESET } from '../constant/Orderconstant'

function Orderscreen() {
    const {id} = useParams()
    const dispatch = useDispatch()


    const [sdkReady, setSdkReady] = useState(false)

    const orderDetails = useSelector(state => state.orderDetails)
    const { order, error, loading } = orderDetails
    const cart = useSelector(state => state.cart)
    const orderPay = useSelector(state => state.orderPay)
    const { loading: loadingPay, success: successPay } = orderPay

    // const orderDeliver = useSelector(state => state.orderDeliver)
    // const { loading: loadingDeliver, success: successDeliver } = orderDeliver

    // const userLogin = useSelector(state => state.userLogin)
    // const { userInfo } = userLogin


    if (!loading && !error) {
        order.itemsPrice = order.orderItems.reduce((acc, item) => acc + item.price * item.qty, 0).toFixed(2)
    }


    const addPayPalScript = () => {
        const script = document.createElement('script')
        script.type = 'text/javascript'
        script.src = 'https://www.paypal.com/sdk/js?client-id=AeDXja18CkwFUkL-HQPySbzZsiTrN52cG13mf9Yz7KiV2vNnGfTDP0wDEN9sGlhZHrbb_USawcJzVDgn'
        script.async = true
        script.onload = () => {
            setSdkReady(true)
        }
        document.body.appendChild(script)
    }

    useEffect(() => {

        // if (!userInfo) {
        //     navigate('/login')
        // }

        if (!order  ||successPay|| order.id !== parseInt(id) ) 
        {
            dispatch({ type: ORDER_PAY_RESET })
            //dispatch({ type: ORDER_DELIVER_RESET })

            dispatch(getOrderDetails(id))
        }
        //  else if (!order.isPaid) {
        //     if (!window.paypal) {
        //         addPayPalScript()
        //     } else {
        //         setSdkReady(true)
        //     }
        // }
    }, [dispatch, order,id,successPay])

    const successPaymentHandler = (paymentResult) => {
        dispatch(payOrder(id, paymentResult))
    }

    // const deliverHandler = () => {
    //     dispatch(deliverOrder(order))
    // }

    return loading ? (
        <Loader />
    ) : error ? (
        <Messageorderscreen var='danger' message={error}></Messageorderscreen>
    ) : (
                <div style={{paddingTop:"5%"}}>
                    <Row>
                        <Col md={8}>
                            <ListGroup variant='flush'>
                                <ListGroup.Item>
                                    <h2>Delivery Address</h2>
                                    <p><strong>Name: </strong> {order.user.name}</p>
                                    <p><strong>Email: </strong>{order.user.email}</p>
                                    <p>
                                        <strong>Shipping: </strong>
                                        {cart.shippingAddress.street},  {order.shippingAddress.city}
                                        {'  '}
                                        {order.shippingAddress.state},
                                        {'  '}
                                        {order.shippingAddress.postalCode}
                                    </p>

                                    {order.isDelivered ? (
                                        <Messageorderscreen var='success' message={"Delivered on"} delivered={order.deliveredAt}></Messageorderscreen>
                                    ) : (
                                            <Messageorderscreen var='danger' message={"Not Delivered"}></Messageorderscreen>
                                        )}
                                </ListGroup.Item>

                                <ListGroup.Item>
                                    <h2>Payment</h2>
                                    <p>
                                        <strong>Method: </strong>
                                        {order.paymentMethod}
                                    </p>
                                    {order.isPaid ? (
                                        <Messageorderscreen var='success' message={"Paid on "} paid={order.paidAt}></Messageorderscreen>
                                    ) : (
                                            <Messageorderscreen var='danger' message={"Not Paid"}></Messageorderscreen>
                                        )}

                                </ListGroup.Item>

                                <ListGroup.Item>
                                    <h2>Order Items</h2>
                                    {order.orderItems.length === 0 ? <Messageorderscreen var='info' message={"Order is empty"}>
                                        
                            </Messageorderscreen> : (
                                            <ListGroup variant='flush'>
                                                {order.orderItems.map((item, index) => (
                                                    <ListGroup.Item key={index}>
                                                        <Row>
                                                            <Col md={1}>
                                                                <Image src={item.img} alt={item.name} fluid rounded />
                                                            </Col>

                                                            <Col>
                                                                {item.name}
                                                            </Col>

                                                            <Col md={4}>
                                                                {item.quantity} X ${item.price} = ${(item.quantity * item.price).toFixed(2)}
                                                            </Col>
                                                        </Row>
                                                    </ListGroup.Item>
                                                ))}
                                            </ListGroup>
                                        )}
                                </ListGroup.Item>

                            </ListGroup>

                        </Col>

                        <Col md={4}>
                            <Card>
                                <ListGroup variant='flush'>
                                    <ListGroup.Item>
                                        <h2>Order Summary</h2>
                                    </ListGroup.Item>

                                    <ListGroup.Item>
                                        <Row>
                                            <Col>Items:</Col>
                                            <Col>${order.itemsPrice}</Col>
                                        </Row>
                                    </ListGroup.Item>

                                    <ListGroup.Item>
                                        <Row>
                                            <Col>Shipping:</Col>
                                            <Col>${order.shippingPrice}</Col>
                                        </Row>
                                    </ListGroup.Item>

                                    <ListGroup.Item>
                                        <Row>
                                            <Col>Tax:</Col>
                                            <Col>${order.taxPrice}</Col>
                                        </Row>
                                    </ListGroup.Item>

                                    <ListGroup.Item>
                                        <Row>
                                            <Col>Total:</Col>
                                            <Col>${order.totalPrice}</Col>
                                        </Row>
                                    </ListGroup.Item>


                                    {!order.isPaid && (
                                        <ListGroup.Item>
                                            {loadingPay && <Loader />}

                                            {!sdkReady ? (
                                                <Loader />
                                            ) : (
                                                    <PayPalButton
                                                        amount={order.totalPrice}
                                                        onSuccess={successPaymentHandler}
                                                    />
                                                )}
                                        </ListGroup.Item>
                                    )}
                                </ListGroup>
                                {/* {loadingDeliver && <Loader />} */}
                                {/* {userInfo && userInfo.isAdmin && order.isPaid && !order.isDelivered && (
                                    <ListGroup.Item>
                                        <Button
                                            type='button'
                                            className='btn btn-block'
                                            onClick={deliverHandler}
                                        >
                                            Mark As Delivered
                                        </Button>
                                    </ListGroup.Item>
                                )} */}
                            </Card>
                        </Col>
                    </Row>
                </div>
            )
}

export default Orderscreen
