 import { RESTAURANT_LIST_REQUEST,
    RESTAURANT_LIST_SUCCESS,
    RESTAURANT_LIST_FAIL, } from '../constant/Restaurantconstant'
 export const restaurantListReducer=(state={restaurants:[]},action)=>{
    switch(action.type){
        case RESTAURANT_LIST_REQUEST:
            return{...state,loading:true,restaurants:[]}
        case RESTAURANT_LIST_SUCCESS:
            return{...state,loading:false,restaurants:action.payload}
        case RESTAURANT_LIST_FAIL:
            return{...state,loading:false,error:action.payload}
        default:
            return state
    }
}
