import axios from "axios";
import { CART_ADD_ITEM } from '../constant/Cartconstant'
export const addtocart=(rid,mid)=>async(dispatch,getState)=>{
const {data}=await axios.get(`/api/restaurant/${rid}`)
dispatch({
    type:CART_ADD_ITEM,
    payload:{
         restaurant:data.name,
    name:data.name,
    // image:fildata.img,
    // price:fildata.price
}
},[dispatch,rid,mid])
localStorage.setItem("cartItems",JSON.stringify(getState().cart.cartItems))
}
