import React from "react";
import brand_img from "../img/brand_img.jpg";
import { Routes, Route, Link } from "react-router-dom";
import "./Navbar.css";
import { listRestaurants } from "../actions/Restaurantactions";
import { useDispatch, useSelector } from "react-redux";
//import db from '../d.json'
import Home from "./Hotel";
import Offer from "../Offer";
import Help from "../Help";
import Hotelmenu from "./Hotelmenu";
import { useState, useEffect } from "react";
import Cartscreen from "./Cartscreen";
import LoginScreen from "./LoginScreen";
function Navbar() {
  const dispatch = useDispatch();
  const restaurantList = useSelector((state) => state.restaurantList);
  const { error, loading, restaurants } = restaurantList;
  // const[restaurants,setrestaurants]=useState([])
  // const[rest,setrest]=useState([])
  // const[leng,setleng]=useState(0)
  useEffect(() => {
    dispatch(listRestaurants());
    // async function fetchRestaurants()
    // {
    //   const {data}=await axios.get('/api/restaurants/')
    //   setrestaurants(data)
    //   setrest(data)
    //   setleng(data.length)
    // }
    // fetchRestaurants()
  }, [dispatch]);
  //const obj=[{'name':'No Item Found','path':'../pictures/no-item.jpg'}]
  const [value, setvalue] = useState(true);
  const [align, setalign] = useState("left");
  const [color, setcolor] = useState("white");
  const [colornav, setnavcolor] = useState("white");
  const [e, sete] = useState(false);
  function togglemode() {
    if (color === "black") {
      setnavcolor("light");
      setcolor("white");
    } else if (color === "white") {
      setnavcolor("dark");
      setcolor("black");
    }
  }
  // function search(data){
  //     setalign("center")
  //     setvalue(true)
  //     if(data==="")
  //     {
  //      setrestaurants([...rest])
  //      setleng(rest.length)
  //     }
  //     else
  // {
  //     let news1= rest.filter((i)=>
  //     {
  //         if(i.name.toLowerCase().indexOf(data)!==-1)
  //         {
  //             return true
  //         }
  //         else
  //         {

  //             return false
  //         }
  //     })
  //     if(news1.length===0)
  //     {
  //       setleng(0)
  //       setrestaurants([...obj])
  //       setvalue(false)
  //       setalign("center")

  //     }
  //     else{
  //         setrestaurants([...news1])
  //         setleng(news1.length)
  // }
  // }
  //   }

  return (
    <>
      <nav
        className={`navbar navbar-expand-lg bg-body-tertiary fixed-top bg-${colornav} text-${
          colornav === "dark" ? "light" : "dark"
        }`}
      >
        <div className="container-fluid">
          <Link className="navbar-brand" to="/" onClick={() => sete(true)}>
            <img
              src={brand_img}
              height="60px"
              width="60px"
              className="rounded-circle"
              alt="brand_img"
            />
            <span
              className="font-weight-bold"
              style={{
                fontFamily: "Verdana, Geneva, Tahoma, sans-serif",
