import React from 'react'
import brand_img from '../img/brand_img.jpg'
import {Routes, Route ,Link} from "react-router-dom";
import './Navbar.css'
//import db from '../d.json'
import Home from './Hotel'
import Offer from '../Offer'
import { useDispatch,useSelector } from 'react-redux'
import Help from '../Help'
import Hotelmenu from './Hotelmenu'
import Cartscreen from './Cartscreen';
import { useState,useEffect } from 'react';
import { listRestaurants } from '../actions/Restaurantactions'
// import axios from "axios";
function Navbar() {
  
   const[restaur,setrestaur]=useState([])
  const dispatch=useDispatch()
  const restaurantList=useSelector(state=>state.restaurantList)
  const{error,loading,restaurants}=restaurantList
  const[rest,setrest]=useState([])
  const[leng,setleng]=useState(0)
  useEffect(()=>{
    dispatch(listRestaurants())
       setrestaur(restaurants)
    //   setrest(data)
    //   setleng(data.length)
    // }
    // fetchRestaurants()
  },[])
const obj=[{'name':'No Item Found','path':'../pictures/no-item.jpg'}]
const[value,setvalue]=useState(true)
const[align,setalign]=useState("left")
const[color,setcolor]=useState('white')
const[colornav,setnavcolor]=useState('white')
const[e,sete]=useState(false)
function togglemode()
{
  if(color==="black")
  {
    setnavcolor('light')
    setcolor('white')
  }
  else if(color==="white")
  {
    setnavcolor('dark')
    setcolor('black')
  }
}
  function search(data){
      setalign("center")
      setvalue(true)
      if(data==="")
      {
       //setrestaurants([...rest])
       setleng(rest.length)
      }
      else
  {
      let news1= rest.filter((i)=>
      {
          if(i.name.toLowerCase().indexOf(data)!==-1)
          {
              return true
          }
          else
          {
  
              return false
          }
      })
      if(news1.length===0)
      {
        setleng(0)
        //setrestaurants([...obj])
        setvalue(false)
        setalign("center")
      
      }
      else{
          //setrestaurants([...news1])
          setleng(news1.length)
  }
  }  
    }
  
  return (
    <><nav className={`navbar navbar-expand-lg bg-body-tertiary fixed-top bg-${colornav} text-${colornav==='dark'?'light':'dark'}`}>
    <div className="container-fluid">
      <Link className="navbar-brand" to="/" onClick={()=>sete(true)}>
        <img src={brand_img} height="60px" width="60px" className="rounded-circle" alt="brand_img" />
        <span className="font-weight-bold" style={{fontFamily:"Verdana, Geneva, Tahoma, sans-serif",color:`${color==='black'?'orange':"#6b3a1b"}`}}> Food
          Order</span>
      </Link>
            <Link id="search2" to="/" style={{textDecoration:"none",width:"30%"}}>
            <div className="dropdown nav-link">
              <form className="d-flex" role="search" autocomplete="off" autocapitalize="Link-zA-Z0-9">
              <input className="form-control me-2" id="search1" type="search" placeholder="Search" aria-label="Search" style={{width:"60%",height:"80%"}} onChange={(e)=>{search(e.target.value)}}
