import React,{useEffect} from 'react'
import { Link,useParams,useLocation } from 'react-router-dom'
import { useDispatch,useSelector } from 'react-redux'
import {Row,Col,ListGroup,Image,Form,Button,Card,} from 'react-bootstrap'
import { addtocart,removefromcart } from '../actions/Cartactions'
import Messagecart from './Messagecart'
function Cartscreen() {
  // const {rid}=useParams()
  // const id=useLocation()
  // const mid=parseInt(id.search.split("=")[1])
const dispatch=useDispatch()
const cart=useSelector(state=>state.cart)
const {cartItems}=cart
// useEffect(()=>{
//   if(rid)
//   {
//     dispatch(addtocart(rid,mid))
//   }
// },[dispatch,rid,mid])
const removefromcarthandler=(id)=>{
  dis
}
  return (
    <Row>
      <Col className="container-fluid" md={8}>
        <p className="display-5" style={{paddingTop:"100px",fontSize:"xx-large"}} align="center">
          SHOPPING CART
          </p>
          {cartItems.length===0?<><Messagecart></Messagecart></>:
          <ListGroup variant="primary"  >
            {
              cartItems.map(item=>
                <ListGroup.Item key={item.id}>
                  <Row>
                    <Col md={2}>
                      <Image src={item.image} alt={item.name} fluid rounded/>
                    </Col>
                    <Col md={3}>{item.name}</Col>
                    <Col md={2}>₹{item.price}</Col>
                    <Col md={3}>
                      <Form.Control as="select" value={item.quantity} onChange={(e)=>dispatch(addtocart(item.restaurantid,item.id,e.target.value))}>
                        <option key="1" value="1">1</option>
                        <option key="2" value="2">2</option>
                        <option key="3" value="3">3</option>
                        <option key="4" value="4">4</option>
                        <option key="5" value="5">5</option>
                        <option key="6" value="6">6</option>
                        <option key="7" value="7">7</option>
                        <option key="8" value="8">8</option>
                        <option key="9" value="9">9</option>
                        <option key="10" value="10">10</option>
                      </Form.Control>
                    </Col>
                    <Col md={1}><Button type="button" variant="light" onClick={()=>removefromcarthandler(item.id)}><svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="25" height="25" viewBox="0 0 30 30">
<path d="M 13 3 A 1.0001 1.0001 0 0 0 11.986328 4 L 6 4 A 1.0001 1.0001 0 1 0 6 6 L 24 6 A 1.0001 1.0001 0 1 0 24 4 L 18.013672 4 A 1.0001 1.0001 0 0 0 17 3 L 13 3 z M 6 8 L 6 24 C 6 25.105 6.895 26 8 26 L 22 26 C 23.105 26 24 25.105 24 24 L 24 8 L 6 8 z"></path>
</svg></Button></Col>
                  </Row>
                </ListGroup.Item>
                )
            }
            </ListGroup>}
          </Col>
    <Col md={4} style={{paddingTop:"164px"}}>
      <Card style={{width:"90%"}}>
        <ListGroup variant="flush">
          <ListGroup.Item>
            <h2>SUBTOTAL({cartItems.reduce((acc,item)=>acc+item.qty,0)})</h2>
          </ListGroup.Item>
        </ListGroup>
      </Card>
    </Col>
    </Row>
  )
}

export default Cartscreen
