import React from 'react'
import  { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { Form, Button,Container, Row, Col } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { saveShippingAddress } from '../actions/Cartactions'
import { useRef } from "react";
import { StandaloneSearchBox, useJsApiLoader } from "@react-google-maps/api";

const libraries = ['places'];
function Shippingscreen() {
  const cart = useSelector(state => state.cart)
  const { shippingAddress } = cart
  const navigate=useNavigate()
  const dispatch = useDispatch()

  const [street, setStreet] = useState(shippingAddress.address)
  const [addresscity, setAddresscity] = useState(shippingAddress.addresscity)
  const [city, setCity] = useState(shippingAddress.city)
  const [state, setState] = useState(shippingAddress.state)
  const [country, setCountry] = useState(shippingAddress.country)
  const [postalCode, setPostalCode] = useState(shippingAddress.postalCode)

  const submitHandler = (e) => {
      e.preventDefault()
      dispatch(saveShippingAddress({ address, city, postalCode, country }))
      navigate('/payment')
  }


  const inputRef = useRef();

    const { isLoaded, loadError } = useJsApiLoader({
        googleMapsApiKey: process.env.AIzaSyD3GG7Qq1XgRMAcjPejT9spgnR4RZ9xzbU,
        libraries
    });

    const handlePlaceChanged = () => { 
        const [ place ] = inputRef.current.getPlaces();
        if(place) { 

            console.log(place.geometry.location.lat())
            console.log(place.geometry.location.lng())
        } 
    }

  return (

      <Container>
            <Row className="justify-content-md-center">
                <Col xs={12} md={6}>
          
          <h1 style={{paddingTop:"30%",color:"orange",userSelect:"none"}}>Delivery Address</h1>
          <Form onSubmit={submitHandler}>

          {isLoaded
        &&
        <StandaloneSearchBox onLoad={ref => inputRef.current = ref} onPlacesChanged={handlePlaceChanged}>
            <input
                type="text"
                className="form-control"
                placeholder="Enter Location"
            />
        </StandaloneSearchBox>}

              <Form.Group controlId='street' className='pt-3'>
                  <Form.Label style={{userSelect:"none"}}>Street</Form.Label>
                  <Form.Control
                      required
                      type='text'
                      placeholder='Enter street'
                      value={stereet ? street : ''}
                      onChange={(e) => setStreet(e.target.value)}
                  >
                  </Form.Control>
              </Form.Group>

              <Form.Group controlId='city' className='pt-3'>
                  <Form.Label style={{userSelect:"none"}}>City</Form.Label>
                  <Form.Control
                      required
                      type='text'
                      placeholder='Enter city'
                      value={city ? city : ''}
                      onChange={(e) => setCity(e.target.value)}
                  >
                  </Form.Control>
              </Form.Group>
              <Form.Group controlId='state' className='pt-3'>
                  <Form.Label style={{userSelect:"none"}}>State</Form.Label>
                  <Form.Control
                      required
                      type='text'
                      placeholder='Enter state'
                      value={state ? state : ''}
                      onChange={(e) => setCountry(e.target.value)}
              ></Form.Control>
                </Form.Group>
              <Form.Group controlId='country' className='pt-3'>
                  <Form.Label style={{userSelect:"none"}}>Country</Form.Label>
                  <Form.Control
                      required
                      type='text'
                      placeholder='Enter country'
                      value={country ? country : ''}
                      onChange={(e) => setCountry(e.target.value)}
                  >
                  </Form.Control>
              </Form.Group>
              <Form.Group controlId='postalCode' className='pt-3 pb-3'>
                  <Form.Label style={{userSelect:"none"}}>Postal Code</Form.Label>
                  <Form.Control
                      required
                      type='text'
                      placeholder='Enter postal code'
                      value={postalCode ? postalCode : ''}
                      onChange={(e) => setPostalCode(e.target.value)}
                  >
                  </Form.Control>
              </Form.Group>

              <Button type='submit' variant='primary' className='btn btn-warning'>
                  Continue
              </Button>
          </Form>
          </Col>
            </Row>
        </Container>
  )
}

export default Shippingscreen
