import React,{useEffect} from 'react'
import { Link,useParams,useLocation } from 'react-router-dom'
import { useDispatch,useSelector } from 'react-redux'
import {Row,Col,ListGroup,Image,Form,Button,Card} from 'react-bootstrap'
import { addtocart } from '../actions/Cartactions'
import Messagecart from './Messagecart'
function Cartscreen() {
  const {rid}=useParams()
  const id=useLocation()
  const mid=id.search.split("=")[1]
const dispatch=useDispatch()
const cart=useSelector(state=>state.cart)
const {cartItems}=cart
useEffect(()=>{
  if(rid)
  {
    dispatch(addtocart(rid,parseInt(mid)))
  }
})
  return (
    <Row>
      <Col className="container-fluid">
        <p className="display-5" style={{paddingTop:"100px",fontSize:"xx-large"}} align="center">
          SHOPPING CART
          {cartItems===0?<Messagecart><Link to="/">GO BACK</Link></Messagecart>:<ListGroup>
            </ListGroup>}
          </p>
          </Col>
    
    </Row>
  )
}

export default Cartscreen
